// Merge and deduplicate news articles
const techArticles = $input.first().json.articles || [];
const businessArticles = $input.last().json.articles || [];

// Combine articles
const allArticles = [...techArticles, ...businessArticles];

// Remove duplicates based on URL
const uniqueArticles = allArticles.filter((article, index, self) => 
  index === self.findIndex((a) => a.url === article.url)
);

// Sort by published date (newest first)
const sortedArticles = uniqueArticles.sort((a, b) => 
  new Date(b.publishedAt) - new Date(a.publishedAt)
);

// Take top 25 articles
const topArticles = sortedArticles.slice(0, 25);

// Format articles for AI analysis
const formattedArticles = topArticles.map((article, index) => ({
  id: index + 1,
  title: article.title,
  description: article.description,
  source: article.source.name,
  publishedAt: article.publishedAt,
  url: article.url,
  content: article.content ? article.content.substring(0, 500) + '...' : ''
}));

return {
  articles: formattedArticles,
  totalCount: formattedArticles.length,
  generatedAt: new Date().toISOString()
};

