// Parse Claude response and format for distribution
const claudeResponse = $json.content[0].text;
const articles = $node["Merge & Process News"].json.articles;

const briefingData = {
  date: new Date().toLocaleDateString('en-US', { 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric',
    weekday: 'long'
  }),
  analysis: claudeResponse,
  sourceCount: articles.length,
  sources: [...new Set(articles.map(a => a.source))],
  generatedAt: new Date().toISOString(),
  articles: articles.slice(0, 10) // Include top 10 articles in summary
};

// Create email subject
const emailSubject = `Daily AI & Tech Briefing - ${new Date().toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}`;

// Format HTML email content
const htmlContent = `
<!DOCTYPE html>
<html>
<head>
    <style>
        body { font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; line-height: 1.6; color: #333; }
        .header { background: #2c3e50; color: white; padding: 20px; text-align: center; }
        .content { padding: 20px; max-width: 800px; margin: 0 auto; }
        .section { margin-bottom: 30px; }
        .section h2 { color: #2c3e50; border-bottom: 2px solid #3498db; padding-bottom: 5px; }
        .articles { background: #f8f9fa; padding: 15px; border-left: 4px solid #3498db; }
        .article { margin-bottom: 15px; padding: 10px; background: white; border-radius: 5px; }
        .meta { color: #666; font-size: 0.9em; }
        ul { padding-left: 20px; }
        li { margin-bottom: 8px; }
    </style>
</head>
<body>
    <div class="header">
        <h1>ü§ñ Daily AI & Technology Briefing</h1>
        <p>${briefingData.date}</p>
        <p>Analyzed ${briefingData.sourceCount} articles from ${briefingData.sources.length} sources</p>
    </div>

    <div class="content">
        <div class="section">
            ${claudeResponse.split('\n').map(line => {
                if (line.startsWith('# ')) {
                    return `<h1>${line.substring(2)}</h1>`;
                } else if (line.startsWith('## ')) {
                    return `<h2>${line.substring(3)}</h2>`;
                } else if (line.startsWith('### ')) {
                    return `<h3>${line.substring(4)}</h3>`;
                } else if (line.startsWith('- ')) {
                    return `<li>${line.substring(2)}</li>`;
                } else if (line.startsWith('* ')) {
                    return `<li>${line.substring(2)}</li>`;
                } else if (line.trim() !== '') {
                    return `<p>${line}</p>`;
                }
                return '';
            }).join('\n')}
        </div>

        <div class="articles">
            <h2>üì∞ Top Articles Referenced</h2>
            ${articles.slice(0, 10).map(article => `
                <div class="article">
                    <h4><a href="${article.url}" target="_blank">${article.title}</a></h4>
                    <p>${article.description}</p>
                    <p class="meta">${article.source} ‚Ä¢ ${new Date(article.publishedAt).toLocaleString()}</p>
                </div>
            `).join('')}
        </div>

        <div class="meta" style="text-align: center; margin-top: 30px; padding-top: 20px; border-top: 1px solid #ddd;">
            <p>Generated by n8n AI Automation ‚Ä¢ ${new Date().toLocaleString()}</p>
        </div>
    </div>
</body>
</html>
`;

// Create Slack formatted message
const slackMessage = {
  text: "Daily AI & Tech Briefing",
  blocks: [
    {
      type: "header",
      text: {
        type: "plain_text",
        text: `ü§ñ Daily AI & Tech Briefing - ${new Date().toLocaleDateString()}`
      }
    },
    {
      type: "context",
      elements: [
        {
          type: "mrkdwn",
          text: `Analyzed ${briefingData.sourceCount} articles from ${briefingData.sources.join(', ')}`
        }
      ]
    },
    {
      type: "divider"
    },
    {
      type: "section",
      text: {
        type: "mrkdwn",
        text: claudeResponse.substring(0, 2900) // Slack message limit
      }
    }
  ]
};

return [{
  briefingData,
  emailSubject,
  htmlContent,
  slackMessage,
  textContent: claudeResponse
}];

