// Extract PR information from GitHub webhook
const payload = $json.body;

if (!payload.pull_request) {
  throw new Error('Not a pull request event');
}

const pr = payload.pull_request;
const repo = payload.repository;

return {
  action: payload.action,
  prNumber: pr.number,
  prTitle: pr.title,
  prBody: pr.body || '',
  prAuthor: pr.user.login,
  prUrl: pr.html_url,
  baseBranch: pr.base.ref,
  headBranch: pr.head.ref,
  headSha: pr.head.sha,
  repoName: repo.name,
  repoFullName: repo.full_name,
  repoOwner: repo.owner.login,
  changedFiles: pr.changed_files,
  additions: pr.additions,
  deletions: pr.deletions,
  createdAt: pr.created_at,
  updatedAt: pr.updated_at
};

// Process PR files and prepare for AI analysis
const prInfo = $node["Extract PR Information"].json;
const prFiles = $node["Get PR Files"].json;
const prDetails = $node["Get PR Details"].json;

// Filter relevant files (exclude certain file types)
const excludeExtensions = ['.png', '.jpg', '.jpeg', '.gif', '.svg', '.ico', '.pdf', '.zip', '.tar.gz'];
const excludePaths = ['node_modules/', 'dist/', 'build/', '.git/', 'coverage/', '__pycache__/'];

const relevantFiles = prFiles.filter(file => {
  const filename = file.filename.toLowerCase();
  const hasExcludedExt = excludeExtensions.some(ext => filename.endsWith(ext));
  const hasExcludedPath = excludePaths.some(path => filename.includes(path));
  return !hasExcludedExt && !hasExcludedPath && file.status !== 'removed';
});

// Categorize files by type
const fileCategories = {
  code: [],
  config: [],
  docs: [],
  tests: []
};

relevantFiles.forEach(file => {
  const filename = file.filename.toLowerCase();

  if (filename.includes('test') || filename.includes('spec') || filename.endsWith('.test.js') || filename.endsWith('.spec.ts')) {
    fileCategories.tests.push(file);
  } else if (filename.endsWith('.md') || filename.endsWith('.rst') || filename.endsWith('.txt')) {
    fileCategories.docs.push(file);
  } else if (filename.includes('config') || filename.endsWith('.json') || filename.endsWith('.yml') || filename.endsWith('.yaml') || filename.endsWith('.toml')) {
    fileCategories.config.push(file);
  } else {
    fileCategories.code.push(file);
  }
});

// Calculate complexity metrics
const complexityMetrics = {
  totalFiles: relevantFiles.length,
  codeFiles: fileCategories.code.length,
  testFiles: fileCategories.tests.length,
  configFiles: fileCategories.config.length,
  docFiles: fileCategories.docs.length,
  totalAdditions: prInfo.additions,
  totalDeletions: prInfo.deletions,
  netChanges: prInfo.additions - prInfo.deletions
};

// Determine review priority
let priority = 'medium';
if (complexityMetrics.totalFiles > 20 || complexityMetrics.totalAdditions > 500) {
  priority = 'high';
} else if (complexityMetrics.totalFiles < 5 && complexityMetrics.totalAdditions < 100) {
  priority = 'low';
}

// Create file summary for AI analysis
const fileSummary = relevantFiles.slice(0, 15).map(file => ({
  filename: file.filename,
  status: file.status,
  additions: file.additions,
  deletions: file.deletions,
  changes: file.changes,
  patch: file.patch ? file.patch.substring(0, 1000) + (file.patch.length > 1000 ? '...' : '') : null
}));

return {
  prInfo,
  prDetails: {
    mergeable: prDetails.mergeable,
    mergeable_state: prDetails.mergeable_state,
    draft: prDetails.draft,
    labels: prDetails.labels?.map(l => l.name) || []
  },
  fileCategories,
  complexityMetrics,
  priority,
  fileSummary,
  analysisContext: {
    repository: prInfo.repoName,
    language: prDetails.base?.repo?.language || 'Unknown',
    hasTests: fileCategories.tests.length > 0,
    hasDocs: fileCategories.docs.length > 0
  }
};

