// Format Claude's review for GitHub comment
const claudeResponse = $json.content[0].text;
const prInfo = $node["Process PR Data"].json.prInfo;
const metrics = $node["Process PR Data"].json.complexityMetrics;

// Determine review status based on Claude's recommendation
const reviewText = claudeResponse.toLowerCase();
let reviewStatus = 'COMMENT';
let requiresChanges = false;

if (reviewText.includes('approve') && !reviewText.includes('request changes')) {
  reviewStatus = 'APPROVE';
} else if (reviewText.includes('request changes') || reviewText.includes('blocking')) {
  reviewStatus = 'REQUEST_CHANGES';
  requiresChanges = true;
}

// Format the GitHub comment
const githubComment = `## ü§ñ AI Code Review

> **Automated review by Claude AI** | PR #${prInfo.prNumber} | ${new Date().toLocaleString()}

### üìä Quick Stats
- **Files Changed:** ${metrics.totalFiles}
- **Lines Added:** +${metrics.totalAdditions}
- **Lines Deleted:** -${metrics.totalDeletions}
- **Priority:** ${$node["Process PR Data"].json.priority.toUpperCase()}
- **Has Tests:** ${$node["Process PR Data"].json.analysisContext.hasTests ? '‚úÖ' : '‚ùå'}

---

${claudeResponse}

---

<details>
<summary>üîß Review Configuration</summary>

- **Model:** Claude 3 Sonnet
- **Analysis Depth:** Comprehensive
- **Security Scanning:** Enabled
- **Best Practices Check:** Enabled

This review was automatically generated. Please consider having a human reviewer also examine this PR, especially for:
- Complex business logic
- Security-sensitive changes
- Architecture decisions
- Performance-critical code

</details>

*Generated by n8n AI Automation*`;

// Create labels based on review
const suggestedLabels = [];
if (requiresChanges) {
  suggestedLabels.push('needs-changes');
}
if (metrics.totalFiles > 10) {
  suggestedLabels.push('large-pr');
}
if (!$node["Process PR Data"].json.analysisContext.hasTests) {
  suggestedLabels.push('needs-tests');
}
if ($node["Process PR Data"].json.priority === 'high') {
  suggestedLabels.push('high-priority');
}

// Determine if human review is needed
const needsHumanReview = 
  requiresChanges || 
  metrics.totalFiles > 15 || 
  metrics.totalAdditions > 300 ||
  reviewText.includes('security') ||
  reviewText.includes('performance') ||
  $node["Process PR Data"].json.priority === 'high';

return {
  prInfo,
  reviewStatus,
  requiresChanges,
  githubComment,
  suggestedLabels,
  needsHumanReview,
  reviewSummary: {
    recommendation: reviewStatus,
    confidence: reviewText.includes('high confidence') ? 'high' : reviewText.includes('low confidence') ? 'low' : 'medium',
    keyIssues: metrics.totalFiles,
    testCoverage: $node["Process PR Data"].json.analysisContext.hasTests,
    securityConcerns: reviewText.includes('security')
  }
};

