// Perform TCP health check using Node.js net module
const net = require('net');
const service = $json;

return new Promise((resolve) => {
  const startTime = Date.now();
  const socket = new net.Socket();

  socket.setTimeout(service.timeout);

  socket.on('connect', () => {
    const responseTime = Date.now() - startTime;
    socket.destroy();
    resolve({
      service: service.name,
      status: 'healthy',
      responseTime,
      timestamp: new Date().toISOString(),
      details: `TCP connection successful to ${service.host}:${service.port}`
    });
  });

  socket.on('timeout', () => {
    socket.destroy();
    resolve({
      service: service.name,
      status: 'unhealthy',
      responseTime: service.timeout,
      timestamp: new Date().toISOString(),
      error: `Connection timeout after ${service.timeout}ms`,
      details: `TCP connection to ${service.host}:${service.port} timed out`
    });
  });

  socket.on('error', (err) => {
    const responseTime = Date.now() - startTime;
    socket.destroy();
    resolve({
      service: service.name,
      status: 'unhealthy',
      responseTime,
      timestamp: new Date().toISOString(),
      error: err.message,
      details: `TCP connection to ${service.host}:${service.port} failed`
    });
  });

  socket.connect(service.port, service.host);
});

// Process HTTP health check results
const service = $node["Split Services"].json;
const response = $json;

const startTime = Date.now();
let status = 'unhealthy';
let details = '';
let error = null;

try {
  // Check if we got a response
  if (response && response.statusCode) {
    const statusCode = response.statusCode;
    const body = response.body;
    const responseTime = response.headers ? parseInt(response.headers['x-response-time']) || 0 : 0;

    // Check status code
    if (service.expectedStatus.includes(statusCode)) {
      status = 'healthy';
      details = `HTTP ${statusCode} response received`;

      // Check content if specified
      if (service.expectedContent) {
        if (typeof body === 'string' && !body.toLowerCase().includes(service.expectedContent.toLowerCase())) {
          status = 'unhealthy';
          details = `HTTP ${statusCode} but unexpected content`;
          error = `Expected content '${service.expectedContent}' not found`;
        } else if (typeof body === 'object' && !JSON.stringify(body).includes(service.expectedContent)) {
          status = 'unhealthy';
          details = `HTTP ${statusCode} but unexpected content`;
          error = `Expected content '${service.expectedContent}' not found in JSON response`;
        }
      }
    } else {
      status = 'unhealthy';
      details = `HTTP ${statusCode} - unexpected status code`;
      error = `Expected status codes: ${service.expectedStatus.join(', ')}`;
    }
  } else {
    status = 'unhealthy';
    details = 'No response received';
    error = 'HTTP request failed - no response data';
  }
} catch (err) {
  status = 'unhealthy';
  details = 'HTTP request error';
  error = err.message || 'Unknown HTTP error';
}

return {
  service: service.name,
  type: service.type,
  critical: service.critical,
  status,
  responseTime: startTime,
  timestamp: new Date().toISOString(),
  details,
  error,
  url: service.url,
  checkData: {
    expectedStatus: service.expectedStatus,
    expectedContent: service.expectedContent,
    timeout: service.timeout
  }
};

// Aggregate all health check results
const allResults = $input.all();
const checkId = $node["Define Services to Monitor"].json.checkId;
const timestamp = $node["Define Services to Monitor"].json.timestamp;

// Process results
const healthyServices = [];
const unhealthyServices = [];
const criticalFailures = [];
const warnings = [];

allResults.forEach(result => {
  const data = result.json;

  if (data.status === 'healthy') {
    healthyServices.push(data);
  } else {
    unhealthyServices.push(data);

    if (data.critical) {
      criticalFailures.push(data);
    } else {
      warnings.push(data);
    }
  }
});

// Calculate health score
const totalServices = allResults.length;
const healthyCount = healthyServices.length;
const healthScore = Math.round((healthyCount / totalServices) * 100);

// Determine overall status
let overallStatus = 'healthy';
if (criticalFailures.length > 0) {
  overallStatus = 'critical';
} else if (unhealthyServices.length > 0) {
  overallStatus = 'warning';
}

// Calculate average response time
const avgResponseTime = Math.round(
  allResults.reduce((sum, result) => sum + (result.json.responseTime || 0), 0) / totalServices
);

// Generate summary
const summary = {
  checkId,
  timestamp,
  overallStatus,
  healthScore,
  statistics: {
    total: totalServices,
    healthy: healthyCount,
    unhealthy: unhealthyServices.length,
    critical: criticalFailures.length,
    warnings: warnings.length,
    avgResponseTime
  },
  services: {
    healthy: healthyServices,
    unhealthy: unhealthyServices,
    critical: criticalFailures,
    warnings
  }
};

return summary;

