// Check for status changes and recovery
const currentStatus = $node["Aggregate Health Results"].json;
const lastStatusData = $node["Get Last Health Status"].json.lastHealthStatus;

let lastStatus = null;
if (lastStatusData && lastStatusData !== 'nil') {
  try {
    lastStatus = JSON.parse(lastStatusData);
  } catch (e) {
    console.warn('Could not parse last status:', e.message);
  }
}

const statusChanged = lastStatus && lastStatus.status !== currentStatus.overallStatus;
const isRecovery = lastStatus && 
  (lastStatus.status === 'critical' || lastStatus.status === 'warning') && 
  currentStatus.overallStatus === 'healthy';

const isDegradation = lastStatus && 
  lastStatus.status === 'healthy' && 
  (currentStatus.overallStatus === 'critical' || currentStatus.overallStatus === 'warning');

return {
  ...currentStatus,
  statusChange: {
    changed: statusChanged,
    isRecovery,
    isDegradation,
    previousStatus: lastStatus?.status || 'unknown',
    currentStatus: currentStatus.overallStatus,
    previousHealthScore: lastStatus?.healthScore || 0,
    healthScoreChange: currentStatus.healthScore - (lastStatus?.healthScore || 0)
  }
};

