// Parse AI analysis and extract structured data
const aiAnalysis = $json.content[0].text;
const issue = $node["GitHub Issue Webhook"].json.body.issue;
const repository = $node["GitHub Issue Webhook"].json.body.repository;

// Simple parsing of AI response (would be more sophisticated in production)
const analysis = {
  issueType: 'other',
  priority: 'medium',
  complexity: 'moderate',
  category: 'other',
  effort: 3,
  suggestedLabels: [],
  autoResponse: '',
  similarIssues: [],
  nextSteps: []
};

// Extract information from AI response
try {
  const lines = aiAnalysis.split('\n');
  lines.forEach(line => {
    const trimmed = line.trim();
    if (trimmed.includes('Issue Type:')) {
      analysis.issueType = trimmed.split(':')[1]?.trim().toLowerCase() || 'other';
    } else if (trimmed.includes('Priority:')) {
      analysis.priority = trimmed.split(':')[1]?.trim().toLowerCase() || 'medium';
    } else if (trimmed.includes('Complexity:')) {
      analysis.complexity = trimmed.split(':')[1]?.trim().toLowerCase() || 'moderate';
    } else if (trimmed.includes('Category:')) {
      analysis.category = trimmed.split(':')[1]?.trim().toLowerCase() || 'other';
    } else if (trimmed.includes('Estimated Effort:')) {
      const effort = trimmed.split(':')[1]?.trim();
      analysis.effort = parseInt(effort?.charAt(0)) || 3;
    } else if (trimmed.includes('Suggested Labels:')) {
      const labels = trimmed.split(':')[1]?.trim();
      analysis.suggestedLabels = labels ? labels.split(',').map(l => l.trim()) : [];
    } else if (trimmed.includes('Auto-Response:')) {
      analysis.autoResponse = trimmed.split(':')[1]?.trim() || '';
    }
  });
} catch (error) {
  console.warn('Could not parse AI analysis fully:', error.message);
}

// Generate comprehensive issue metadata
const issueMetadata = {
  issueNumber: issue.number,
  title: issue.title,
  author: issue.user.login,
  repository: repository.full_name,
  url: issue.html_url,
  createdAt: issue.created_at,
  existingLabels: (issue.labels || []).map(l => l.name),
  analysis,
  recommendations: {
    shouldAutoLabel: analysis.suggestedLabels.length > 0,
    shouldAutoAssign: analysis.priority === 'critical' || analysis.complexity === 'simple',
    shouldAutoRespond: analysis.autoResponse.length > 0,
    needsTriaging: analysis.priority === 'critical' || analysis.issueType === 'bug'
  },
  rawAnalysis: aiAnalysis
};

return issueMetadata;

