// Define repositories to monitor
const repositories = [
  'your-org/agentic-ai-stack',
  'your-org/claude-proxy',
  'your-org/n8n-workflows'
  // Add more repositories as needed
];

return {
  repositories,
  checkTimestamp: new Date().toISOString(),
  metrics: [
    'open_issues',
    'open_pull_requests',
    'recent_commits',
    'stale_issues',
    'security_alerts'
  ]
};

// Analyze repository health
const repoName = $node["Split Repository Checks"].json;
const repoInfo = $node["Get Repository Info"].json;
const openIssues = $node["Get Open Issues"].json || [];

// Calculate repository metrics
const now = new Date();
const oneWeekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
const oneMonthAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);

const metrics = {
  repository: repoName,
  stars: repoInfo.stargazers_count || 0,
  forks: repoInfo.forks_count || 0,
  openIssues: repoInfo.open_issues_count || 0,
  language: repoInfo.language || 'Unknown',
  lastUpdate: repoInfo.updated_at,
  defaultBranch: repoInfo.default_branch || 'main',
  hasWiki: repoInfo.has_wiki || false,
  hasProjects: repoInfo.has_projects || false,
  visibility: repoInfo.visibility || 'private'
};

// Analyze issues
const issueAnalysis = {
  totalOpen: openIssues.length,
  bugs: openIssues.filter(issue => 
    issue.labels?.some(label => label.name.toLowerCase().includes('bug'))
  ).length,
  features: openIssues.filter(issue => 
    issue.labels?.some(label => 
      label.name.toLowerCase().includes('feature') || 
      label.name.toLowerCase().includes('enhancement')
    )
  ).length,
  staleIssues: openIssues.filter(issue => 
    new Date(issue.updated_at) < oneMonthAgo
  ).length,
  recentIssues: openIssues.filter(issue => 
    new Date(issue.created_at) > oneWeekAgo
  ).length,
  needsResponse: openIssues.filter(issue => 
    issue.comments === 0 && new Date(issue.created_at) < new Date(now.getTime() - 2 * 24 * 60 * 60 * 1000)
  ).length
};

// Calculate health score
let healthScore = 100;

// Deduct points for issues
if (issueAnalysis.staleIssues > 5) healthScore -= 20;
if (issueAnalysis.needsResponse > 3) healthScore -= 15;
if (issueAnalysis.bugs > 10) healthScore -= 15;
if (issueAnalysis.totalOpen > 50) healthScore -= 10;
if (new Date(metrics.lastUpdate) < oneWeekAgo) healthScore -= 10;

healthScore = Math.max(0, healthScore);

// Determine alerts
const alerts = [];

if (issueAnalysis.staleIssues > 10) {
  alerts.push({
    type: 'stale_issues',
    severity: 'warning',
    message: `${issueAnalysis.staleIssues} issues haven't been updated in over a month`
  });
}

if (issueAnalysis.needsResponse > 5) {
  alerts.push({
    type: 'unresponded_issues',
    severity: 'warning',
    message: `${issueAnalysis.needsResponse} issues need initial response`
  });
}

if (issueAnalysis.bugs > 15) {
  alerts.push({
    type: 'high_bug_count',
    severity: 'high',
    message: `High number of open bugs: ${issueAnalysis.bugs}`
  });
}

return {
  repository: repoName,
  metrics,
  issueAnalysis,
  healthScore,
  alerts,
  checkTimestamp: new Date().toISOString()
};

// Aggregate all repository health data
const allResults = $input.all();
const timestamp = $node["Define Repositories to Monitor"].json.checkTimestamp;

const healthSummary = {
  timestamp,
  totalRepositories: allResults.length,
  averageHealthScore: Math.round(
    allResults.reduce((sum, repo) => sum + repo.json.healthScore, 0) / allResults.length
  ),
  totalOpenIssues: allResults.reduce((sum, repo) => sum + repo.json.issueAnalysis.totalOpen, 0),
  totalStaleIssues: allResults.reduce((sum, repo) => sum + repo.json.issueAnalysis.staleIssues, 0),
  totalBugs: allResults.reduce((sum, repo) => sum + repo.json.issueAnalysis.bugs, 0),
  repositoriesWithAlerts: allResults.filter(repo => repo.json.alerts.length > 0).length,
  repositories: allResults.map(repo => repo.json)
};

// Identify top concerns
const topConcerns = [];

if (healthSummary.averageHealthScore < 70) {
  topConcerns.push('Overall repository health is below average');
}

if (healthSummary.totalStaleIssues > 20) {
  topConcerns.push(`${healthSummary.totalStaleIssues} stale issues across repositories`);
}

if (healthSummary.totalBugs > 30) {
  topConcerns.push(`High bug count: ${healthSummary.totalBugs} open bugs`);
}

healthSummary.topConcerns = topConcerns;

return healthSummary;

